import { useState, useEffect, useRef } from "react";
import { API_GET_DATA } from "./constants";
import "./index.css";
import Edit from "./Home/Edit.js";
import List from "./Home/List.js";
import "./index.css";

// 從資料庫抓資料
async function fetchData(setData) {
  const response = await fetch(API_GET_DATA);
  const { data } = await response.json();
  setData(data);
}

// 將資料put到資料庫
async function putData(data) {
  await fetch(API_GET_DATA, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ data }),
  });
}

const App2 = () => {
  const [data, setData] = useState([]);
  const submittingstatus = useRef(false); //送資料的狀態

  // 從資料庫抓出資料, 只能出現一次
  useEffect(() => {
    fetchData(setData);
  }, []);

  // Until True;
  // useState有變動的資料, put進去資料庫
  useEffect(() => {
    if (!submittingstatus.current) {
      //因為後面設定add/delete都是true, 所以原始值是false沒錯
      return;
    }
    putData(data).then((data) => (submittingstatus.current = false));
  }, [data]);

  return (
    <div className="text-white w-full p-5 mr-4 justify-center">
      <Edit add={setData} submittingstatus={submittingstatus} />
      <List
        add={setData} //剛剛做的變動
        listData={data}
        deleteData={setData}
        submittingstatus={submittingstatus}
      />
    </div>
  );
};

export default App2;

// true --> 就會執行
// false --> 就會停滯
