import { useState, useContext } from "react";
import { QuizContext } from "../Helpers/Contexts";
import { Questions } from "../Helpers/QuestionBanks";

function EndScreen() {
  const { score, setScore, gameState, setGameState } = useContext(QuizContext);
  function restartQuiz() {
    setScore(0);
    setGameState("menu");
  }
  return (
    <div className="EndScreen">
      {""}
      <h1 className="Title">You did it!</h1>
      <h3 className="text-white">
        correct answer:{score}/Total Question:{Questions.length}
      </h3>
      <button onClick={restartQuiz}>Restart the Quiz</button>
    </div>
  );
}

export default EndScreen;
