import { useState, useContext } from "react";
import { Questions } from "../Helpers/QuetionBanks";

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0); //0 is first object in array
  function changeQ() {
    currentQuestion = currentQuestion + 1;
    setCurrentQuestion(currentQuestion);
  }
  // the idea is everytime you click, the state will increment 1

  return (
    <div className="Quiz">
      <h1>{Questions[currentQuestion].prompt}</h1>
      <button className="block" onClick={changeQ}>
        {Questions[currentQuestion].optionA}
      </button>
      <button className="block">{Questions[currentQuestion].optionB}</button>
      <button className="block">{Questions[currentQuestion].optionC}</button>
      <button className="block">{Questions[currentQuestion].optionD}</button>
    </div>
  );
}

export default Quiz;
