import { useState, useContext } from "react";
import { QuizContext } from "../Helpers/Contexts";
import { Questions } from "../Helpers/QuetionBanks";

function Quiz() {
  const { score, setScore } = useContext(QuizContext);
  const [currentQuestion, setCurrentQuestion] = useState(0); //0 is first object in array
  const [optionChosen, setOptionChosen] = useState("");

  function nextQuestion() {
    if (Questions[currentQuestion].answer === optionChosen) {
      setScore(score + 1); // 原本的score + 1
    }
    alert(score);
    setCurrentQuestion(currentQuestion + 1);
  }

  function lastQuestion() {
    setCurrentQuestion(currentQuestion - 1);
  }

  return (
    <div className="Quiz">
      <h1>{Questions[currentQuestion].prompt}</h1>
      <div>
        <button
          onClick={() => {
            nextQuestion();
            setOptionChosen("A");
          }}
          className="block"
        >
          {Questions[currentQuestion].optionA}
        </button>
        <button
          onClick={() => {
            nextQuestion();
            setOptionChosen("B");
          }}
          className="block"
        >
          {Questions[currentQuestion].optionB}
        </button>
        <button
          onClick={() => {
            nextQuestion();
            setOptionChosen("C");
          }}
          className="block"
        >
          {Questions[currentQuestion].optionC}
        </button>
        <button
          onClick={() => {
            nextQuestion();
            setOptionChosen("D");
          }}
          className="block"
        >
          {Questions[currentQuestion].optionD}
        </button>
      </div>
      {(() => {
        if (currentQuestion == Questions.length - 1) {
          return (
            <div>
              <button>Finish Quiz!</button>
              <button onClick={lastQuestion}>Last</button>
            </div>
          );
        } else if (currentQuestion !== 0) {
          return (
            <div>
              <button onClick={lastQuestion}>Last</button>
            </div>
          );
        }
      })()}
    </div>
  );
}

export default Quiz;
