import { useState } from "react";
import "tw-elements";
import Whatever from "./Whatever";

const Item = ({
  id,
  note,
  date,
  time,
  deleteData,
  submittingstatus,
  key,
  listData,
  add,
  item,
  finish,
  setFinish,
