import { useState, useContext } from "react";
import { QuizContext } from "../Helpers/Contexts";
import { Questions } from "../Helpers/QuestionBanks";

function Quiz() {
  const { score, setScore, gameState, setGameState } = useContext(QuizContext);
  const [currentQuestion, setCurrentQuestion] = useState(0); //0 is first object in array
  const [optionChosen, setOptionChosen] = useState("");

  function chooseOption(option) {
    setOptionChosen(option);
  }

  // function nextQuestion() {
  //   if (Questions[currentQuestion].answer == optionChosen) {
  //     setScore(score + 1);
  //   }
  //   if (currentQuestion == Questions.length - 1) {
  //     finishQuiz();
  //   }
  //   alert(score);
  //   setCurrentQuestion(currentQuestion + 1);
  // }
  const nextQuestion = () => {
    if (Questions[currentQuestion].answer == optionChosen) {
      setScore(score + 1);
    }
    alert(score);
    setCurrentQuestion(currentQuestion + 1);
  };

  // function prevQuestion() {
  //   if (Questions[currentQuestion].answer === optionChosen) {
  //     setScore(score - 1); // 原本的score + 1
  //   }
  //   setCurrentQuestion(currentQuestion - 1);
  // }

  const finishQuiz = () => {
    if (Questions[currentQuestion].answer == optionChosen) {
      setScore(score + 1);
    }
    setGameState("endScreen");
  };

  // function finishQuiz() {
  //   if (Questions[currentQuestion].answer == optionChosen) {
  //     setScore(score + 1); // 原本的score + 1
  //   }
  //   setGameState("endScreen");
  // }

  return (
    <div className="Quiz">
      <h1 className="Title">{Questions[currentQuestion].prompt}</h1>
      <div className="options">
        <button
          onClick={() => {
            chooseOption("A");
            nextQuestion();
          }}
        >
          {Questions[currentQuestion].optionA}
        </button>
        <button
          onClick={() => {
            chooseOption("B");
            nextQuestion();
          }}
        >
          {Questions[currentQuestion].optionB}
        </button>
        <button
          onClick={() => {
            chooseOption("C");
            nextQuestion();
          }}
        >
          {Questions[currentQuestion].optionC}
        </button>
        <button
          onClick={() => {
            chooseOption("D");
            nextQuestion();
          }}
        >
          {Questions[currentQuestion].optionD}
        </button>
      </div>

      {currentQuestion == Questions.length - 1 ? (
        <button onClick={finishQuiz} id="nextQuestion">
          Finish Quiz
        </button>
      ) : (
        <button onClick={nextQuestion} id="nextQuestion">
          Next Question
        </button>
      )}
    </div>
  );
}

export default Quiz;
