import { useState, useContext } from "react";
import { Questions } from "../Helpers/QuetionBanks";

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0); //0 is first object in array
  const [optionChosen, setOptionChosen] = useState("");

  function changeQ() {
    currentQuestion = currentQuestion + 1;
    setCurrentQuestion(currentQuestion);
  }
  // the idea is everytime you click, the state will increment 1

  return (
    <div className="Quiz">
      <h1>{Questions[currentQuestion].prompt}</h1>
      <div>
        <button onClick={() => setOptionChosen("A")} className="block">
          {Questions[currentQuestion].optionA}
        </button>
        <button onClick={() => setOptionChosen("B")} className="block">
          {Questions[currentQuestion].optionB}
        </button>
        <button onClick={() => setOptionChosen("C")} className="block">
          {Questions[currentQuestion].optionC}
        </button>
        <button onClick={() => setOptionChosen("D")} className="block">
          {Questions[currentQuestion].optionD}
        </button>
      </div>
    </div>
  );
}

export default Quiz;
